package com.abc;

public class Elseif {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num = 0;
		
		if (num>0) {
			System.out.println("number is positive");
			
		}
		else if (num<0) {
			System.out.println("number is negative");
		}
		else {
			System.out.println("Number is zero");
		}

	}

}
